mapper.select:
SELECT id, merchant_id, user_id, table_id, order_no, total_amount, status, create_time FROM t_order WHERE table_id = #{table_id}
AND status = #{status}
ORDER BY create_time DESC LIMIT 1

SELECT 
    t_order.*,
    t_table.number as table_number
FROM t_order
LEFT JOIN t_table ON t_order.table_id = t_table.id
WHERE t_order.merchant_id = #{merchantId}

    and t_order.status = #{status}

ORDER BY t_order.create_time DESC

    limit #{start}, #{pageSize}

select count(*) from t_order
where merchant_id = #{merchantId}

    and status = #{status}
