// store/modules/user.js
export default {
    state: {
        token: localStorage.getItem('token'),
        merchantInfo: null
    },
    getters: {
        isLoggedIn: state => !!state.token && !!state.merchantInfo
    },
    mutations: {
        SET_TOKEN(state, token) {
            state.token = token
        },
        SET_MERCHANT(state, merchantInfo) {
            state.merchantInfo = merchantInfo
        },
        LOGOUT(state) {
            state.token = null
            state.merchantInfo = null
        }
    }
}
