import { mapGetters } from 'vuex'

export default {
  data() {
    return {
      categoryList: [],
      dialogVisible: false,
      dialogType: 'add', // add or edit
      submitLoading: false,
      categoryForm: {
        id: '',
        name: '',
        sort: 1
      },
      rules: {
        name: [
          { required: true, message: '请输入分类名称', trigger: 'blur' },
          { min: 2, max: 20, message: '长度在 2 到 20 个字符', trigger: 'blur' }
        ],
        sort: [
          { required: true, message: '请输入排序号', trigger: 'blur' }
        ]
      }
    }
  },
  
  computed: {
    ...mapGetters(['currentMerchant']),
    dialogTitle() {
      return this.dialogType === 'add' ? '新增分类' : '编辑分类'
    }
  },
  
  created() {
    this.getCategoryList()
  },
  
  methods: {
    // 获取分类列表
    async getCategoryList() {
      try {
        const res = await new this.Request(
          this.Urls.category.list(this.currentMerchant.id)
        ).modeget()
        
        if (res.code === 0) {
          this.categoryList = res.data
        }
      } catch (error) {
        console.error('获取分类列表失败：', error)
        this.$message.error('获取分类列表失败')
      }
    },
    
    // 显示新增弹窗
    showAddDialog() {
      this.dialogType = 'add'
      this.categoryForm = {
        name: '',
        sort: 1
      }
      this.dialogVisible = true
    },
    
    // 显示编辑弹窗
    showEditDialog(category) {
      this.dialogType = 'edit'
      this.categoryForm = { ...category }
      this.dialogVisible = true
    },
    
    // 提交表单
    submitForm() {
      this.$refs.categoryForm.validate(async valid => {
        if (valid) {
          this.submitLoading = true
          try {
            const url = this.dialogType === 'add' 
              ? this.Urls.category.add 
              : this.Urls.category.update
            
            const method = this.dialogType === 'add' ? 'modepost' : 'modeput'
            const data = {
              ...this.categoryForm,
              merchantId: this.currentMerchant.id
            }
            
            const res = await new this.Request(url)[method](data)
            
            if (res.code === 0) {
              this.$message.success(`${this.dialogType === 'add' ? '新增' : '编辑'}成功`)
              this.dialogVisible = false
              this.getCategoryList()
            } else {
              this.$message.error(res.msg || `${this.dialogType === 'add' ? '新增' : '编辑'}失败`)
            }
          } catch (error) {
            this.$message.error(`${this.dialogType === 'add' ? '新增' : '编辑'}失败`)
          } finally {
            this.submitLoading = false
          }
        }
      })
    },
    
    // 删除分类
    async deleteCategory(category) {
      try {
        await this.$confirm('确认删除该分类吗？删除后无法恢复', '提示', {
          type: 'warning'
        })
        
        const res = await new this.Request(
          this.Urls.category.delete(category.id)
        ).modedelete()
        
        if (res.code === 0) {
          this.$message.success('删除成功')
          this.getCategoryList()
        } else {
          this.$message.error(res.msg || '删除失败')
        }
      } catch (error) {
        if (error !== 'cancel') {
          this.$message.error('删除失败')
        }
      }
    }
  }
}
